    private void jtLivroMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jtLivroMouseClicked
        if(jtLivro.getSelectedRow()!= -1){
            txtNumeroDeTombo.setText(jtLivro.getValueAt(jtLivro.getSelectedRow(), 0).toString());
            txtTitulo.setText(jtLivro.getValueAt(jtLivro.getSelectedRow(), 1).toString());
            txtAutor.setText(jtLivro.getValueAt(jtLivro.getSelectedRow(), 2).toString());
            txtEditora.setText(jtLivro.getValueAt(jtLivro.getSelectedRow(), 3).toString());
            txtEdicao.setText(jtLivro.getValueAt(jtLivro.getSelectedRow(), 4).toString());
            jcStatus.setSelectedItem(jtLivro.getValueAt(jtLivro.getSelectedRow(), 5).toString());
        }
    }//GEN-LAST:event_jtLivroMouseClicked

