    private void jbPesquisarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbPesquisarActionPerformed

        int p =verificarCampoPesquisar();

        System.out.println(p + " p");
        livro.setPesquisa(txtPesquisar.getText());
        BeansLivro model =  dao.buscaLivro(livro);
        //        txtNumeroDeTombo.setText(String.valueOf(model.getNumeroDeTombo()));
        if(p==0 && dao.buscaLivro(livro) != null && jcTipoDePesquisa.getSelectedItem().toString()=="Número de tombo" ){

            System.out.println(jcTipoDePesquisa.getSelectedItem().toString());
            txtNumeroDeTombo.setText(String.valueOf(model.getNumeroDeTombo()));
            txtTitulo.setText(model.getTitulo());
            txtAutor.setText(model.getAutor());
            txtEditora.setText(model.getEditora());
            txtEdicao.setText(String.valueOf(model.getEdicao()));
            jcStatus.setSelectedItem(model.getStatus());
            jtLivro.setVisible(true);
            jbAlterar.setEnabled(true);
            jbExcluir.setEnabled(true);
            jbInserir.setEnabled(!true);
            jBCancelar.setEnabled(true);
            livro.setPesquisa("");
            //preencherTabela("SELECT  numeroDeTombo, titulo, autor, editora, edicao, status " + "FROM livros " +"WHERE " +"(titulo ='" + livro.getPesquisa() + "')");
            preencherTabela("select * from livros where numeroDeTombo = " + livro.getNumeroDeTombo());
            txtPesquisar.setText("");
        }else if(p==0 && jcTipoDePesquisa.getSelectedItem().toString()=="Título" ){
            System.out.println(jcTipoDePesquisa.getSelectedItem().toString());
            jtLivro.setVisible(true);
            jbAlterar.setEnabled(true);
            jbExcluir.setEnabled(true);
            jbInserir.setEnabled(!true);
            jBCancelar.setEnabled(true);
            livro.setPesquisa("");
            //preencherTabela("SELECT  numeroDeTombo, titulo, autor, editora, edicao, status " + "FROM livros " +"WHERE " +"(titulo ='" + livro.getPesquisa() + "')");
            preencherTabela("SELECT * FROM `livros` WHERE titulo LIKE '%" + txtPesquisar.getText()+"%'");
            txtPesquisar.setText("");

        }else{
            JOptionPane.showMessageDialog(null, "Item não encontrado!");
            jtLivro.setVisible(false);
            txtPesquisar.setText("");
        }
    }//GEN-LAST:event_jbPesquisarActionPerformed

