    private void jbConfirmarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbConfirmarActionPerformed
        int r = verificarCampos();
        livro.setPesquisa(txtPesquisar.getText());
        BeansLivro model =  dao.buscaLivro(livro);
        if(flag==1 && r !=1){

            livro.setNumeroDeTombo((Integer.parseInt(txtNumeroDeTombo.getText())));
            livro.setTitulo(txtTitulo.getText());
            livro.setAutor(txtAutor.getText());
            livro.setEditora(txtEditora.getText());
            livro.setEdicao((Integer.parseInt(txtEdicao.getText())));
            livro.setStatus("Ativo");

            dao.salvar(livro);

            limparCampos();
            preencherTabela("select * from livros order by numeroDeTombo");
            txtNumeroDeTombo.setEnabled(false);
            txtTitulo.setEnabled(false);
            txtAutor.setEnabled(false);
            txtEditora.setEnabled(false);
            txtEdicao.setEnabled(false);
            jcStatus.setEnabled(false);
            jbConfirmar.setEnabled(false);

        }else if (flag == 2 && r!=1){
            livro.setNumeroDeTombo((Integer.parseInt(txtNumeroDeTombo.getText())));
            livro.setTitulo(txtTitulo.getText());
            livro.setAutor(txtAutor.getText());
            livro.setEditora(txtEditora.getText());
            livro.setEdicao((Integer.parseInt(txtEdicao.getText())));
            livro.setStatus((String)jcStatus.getSelectedItem());
            dao.editar(livro);
            limparCampos();
            preencherTabela("select * from livros order by numeroDeTombo");
            txtNumeroDeTombo.setEnabled(false);
            txtTitulo.setEnabled(false);
            txtAutor.setEnabled(false);
            txtEditora.setEnabled(false);
            txtEdicao.setEnabled(false);
            jcStatus.setEnabled(false);
            jbConfirmar.setEnabled(false);
            jbInserir.setEnabled(true);
        }else if (flag == 3 && r!=1){

            dao.excluir(livro);
            limparCampos();
            preencherTabela("select * from livros order by numeroDeTombo");
            txtNumeroDeTombo.setEnabled(false);
            txtTitulo.setEnabled(false);
            txtAutor.setEnabled(false);
            txtEditora.setEnabled(false);
            txtEdicao.setEnabled(false);
            jcStatus.setEnabled(false);
            jbConfirmar.setEnabled(false);
            jbInserir.setEnabled(true);
            jbAlterar.setEnabled(true);
            jbExcluir.setEnabled(true);

        }

    }//GEN-LAST:event_jbConfirmarActionPerformed

