            txtNomePolitica.setText(jtPolitica.getValueAt(jtPolitica.getSelectedRow(), 0).toString());
            txtQtdDia.setText(jtPolitica.getValueAt(jtPolitica.getSelectedRow(), 1).toString());
            txtQtdItens.setText(jtPolitica.getValueAt(jtPolitica.getSelectedRow(), 2).toString());
