    private void jbConfirmarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbConfirmarActionPerformed
        int r = verificarCampos();

        if(flag==1 && r !=1){

            politica.setNome(txtNomePolitica.getText());
            politica.setQtdDeDia(Integer.parseInt(txtQtdDia.getText().toString()));
            politica.setQtdDeLivro(Integer.parseInt(txtQtdItens.getText().toString()));
            politica.setMultaEmDinheiro(Float.parseFloat(txtMultaPorDinheiro.getText()));
            politica.setStatus("Inativa");
            politica.setMultaPorDia(Integer.parseInt(jsMultaEmDias.getText()));
            System.out.println(politica.getMultaPorDia());
            daoPolitica.salvar(politica);

            limparCampos();
            // preencherTabela("SELECT Nome, Qtddias, Qtdlivros, multaEmDias, multaDinheiro, Status " + "FROM politicas " +"WHERE " +"(nome ='" + politica.getPesquisa() + "')");
            preencherTabela("SELECT * FROM `politicas` WHERE Nome LIKE '%" + txtPesquisar.getText()+"%'");
            txtNomePolitica.setEnabled(false);
            txtQtdDia.setEnabled(false);
            txtQtdItens.setEnabled(false);
            txtMultaPorDinheiro.setEnabled(false);
            txtPesquisar.setEnabled(true);
            jbPesquisar.setEnabled(true);
            jbConfirmar.setEnabled(false);
            jsMultaEmDias.setEnabled(false);
        }else if (flag == 2 && r!=1){
            politica.setNome(txtNomePolitica.getText());
            politica.setQtdDeDia(Integer.parseInt(txtQtdDia.getText()));
            politica.setQtdDeLivro(Integer.parseInt(txtQtdItens.getText()));
            politica.setMultaEmDinheiro(Float.parseFloat(txtMultaPorDinheiro.getText()));
            politica.setStatus(jtPolitica.getValueAt(jtPolitica.getSelectedRow(), 5).toString());
            daoPolitica.editar(politica);
            jbInserir.setEnabled(true);
            limparCampos();
            //preencherTabela("SELECT Nome, Qtddias, Qtdlivros, multaEmDias, multaDinheiro, Status " + "FROM politicas " +"WHERE " +"(nome ='" + politica.getPesquisa() + "')");
            preencherTabela("SELECT * FROM `politicas` WHERE Nome LIKE '%" + txtPesquisar.getText()+"%'");
            txtNomePolitica.setEnabled(false);
            txtQtdDia.setEnabled(false);
            txtQtdItens.setEnabled(false);
            txtMultaPorDinheiro.setEnabled(false);
            jsMultaEmDias.setEnabled(false);
            jbConfirmar.setEnabled(false);
        }else if (flag == 3 ){
            politica.setPesquisa(txtNomePolitica.getText());
            BeansPolitica model =  daoPolitica.buscaPolitica(politica);
            String p = model.getStatus();

            if("Inativa".equals(p)){
                daoPolitica.excluir(politica);
                limparCampos();

                preencherTabela("SELECT * FROM `politicas` WHERE Nome LIKE '%" + txtPesquisar.getText()+"%'");
                txtNomePolitica.setEnabled(false);
                txtQtdDia.setEnabled(false);
                txtQtdItens.setEnabled(false);
                txtMultaPorDinheiro.setEnabled(false);

                jbConfirmar.setEnabled(false);
                jbInserir.setEnabled(true);
                jbAlterar.setEnabled(false);
                jbExcluir.setEnabled(false);

            }else if("Ativa".equals(p)){

                JOptionPane.showMessageDialog(null, "Politica de emprestimo em uso! exclusão não realizada ");
            }

        }
    }//GEN-LAST:event_jbConfirmarActionPerformed

